/*jslint browser: true */

(function () {
    'use strict';
    var url = '',
        host = location.hostname,
        domain = document.domain,
        sites = {},
        scriptElement;

    sites = {
        youtube: {
            url: 'https://spun.github.io/dsc/sites/youtube.min.js'
        },
        goear: {
            url: 'https://spun.github.io/dsc/sites/goear.min.js'
        },
        twitch: {
            url: 'https://spun.github.io/dsc/sites/twitch.min.js'
        },
        livestream: {
            url: 'https://spun.github.io/dsc/sites/livestream.min.js'
        }
    };

    if (host === 'www.youtube.com' || host === 'youtube.com') {
        url = sites.youtube.url;
    } else if (host === 'www.goear.com' || host === 'goear.com') {
        url = sites.goear.url;
    } else if (domain === 'twitch.tv') {
        url = sites.twitch.url;
    } else if (domain === 'new.livestream.com') {
        url = sites.livestream.url;
    }

    if (url !== '') {
        scriptElement = document.createElement('script');
        scriptElement.setAttribute("src", url);
        document.body.appendChild(scriptElement);
    }
}());